--Create Intwenty System tables
CREATE TABLE sysmodel_SystemItem (Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,Title TEXT NOT NULL ,TitleLocalizationKey TEXT NULL ,Description TEXT NULL ,MetaCode TEXT NOT NULL ,DbPrefix TEXT NOT NULL )
CREATE TABLE sysmodel_ApplicationItem (SystemMetaCode TEXT NOT NULL ,MetaCode TEXT NOT NULL ,Id INTEGER NOT NULL ,Title TEXT NOT NULL ,TitleLocalizationKey TEXT NULL ,Description TEXT NULL ,DbName TEXT NOT NULL ,DataMode INTEGER NULL ,UseVersioning INTEGER NULL ,TenantIsolationLevel INTEGER NULL ,TenantIsolationMethod INTEGER NULL , PRIMARY KEY (Id))
CREATE TABLE sysmodel_DatabaseItem (Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,SystemMetaCode TEXT NOT NULL ,AppMetaCode TEXT NOT NULL ,MetaCode TEXT NOT NULL ,MetaType TEXT NOT NULL ,Description TEXT NULL ,ParentMetaCode TEXT NOT NULL ,DbName TEXT NULL ,DataType TEXT NULL ,Properties TEXT NULL )
CREATE TABLE sysmodel_ViewItem (Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,SystemMetaCode TEXT NOT NULL ,AppMetaCode TEXT NOT NULL ,MetaCode TEXT NOT NULL ,MetaType TEXT NOT NULL ,Title TEXT NULL ,TitleLocalizationKey TEXT NULL ,Description TEXT NULL ,DescriptionLocalizationKey TEXT NULL ,Path TEXT NOT NULL ,FilePath TEXT NULL ,IsPrimary INTEGER NULL ,IsPublic INTEGER NULL ,Properties TEXT NULL )
CREATE TABLE sysmodel_UserInterfaceItem (Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,SystemMetaCode TEXT NOT NULL ,AppMetaCode TEXT NOT NULL ,MetaCode TEXT NOT NULL ,MetaType TEXT NOT NULL ,ViewMetaCode TEXT NOT NULL ,DataTableMetaCode TEXT NULL )
CREATE TABLE sysmodel_FunctionItem (Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,SystemMetaCode TEXT NOT NULL ,AppMetaCode TEXT NOT NULL ,MetaCode TEXT NOT NULL ,MetaType TEXT NOT NULL ,OwnerMetaType TEXT NOT NULL ,OwnerMetaCode TEXT NOT NULL ,Title TEXT NOT NULL ,TitleLocalizationKey TEXT NULL ,IsModalAction INTEGER NULL ,ActionMetaCode TEXT NULL ,ActionMetaType TEXT NULL ,ActionPath TEXT NULL ,Properties TEXT NULL )
CREATE TABLE sysmodel_UserInterfaceStructureItem (Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,SystemMetaCode TEXT NOT NULL ,AppMetaCode TEXT NOT NULL ,MetaCode TEXT NOT NULL ,MetaType TEXT NOT NULL ,Title TEXT NULL ,TitleLocalizationKey TEXT NULL ,Description TEXT NULL ,UserInterfaceMetaCode TEXT NOT NULL ,ParentMetaCode TEXT NOT NULL ,DataTableMetaCode TEXT NULL ,DataColumn1MetaCode TEXT NULL ,DataColumn2MetaCode TEXT NULL ,ColumnOrder INTEGER NULL ,RowOrder INTEGER NULL ,Domain TEXT NULL ,Properties TEXT NULL ,RawHTML TEXT NULL )
CREATE TABLE sysmodel_ValueDomainItem (Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,DomainName TEXT NULL ,Code TEXT NULL ,Value TEXT NULL ,Properties TEXT NULL ,ValueLocalizationKey TEXT NULL )
CREATE TABLE sysmodel_EndpointItem (Id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,SystemMetaCode TEXT NOT NULL ,AppMetaCode TEXT NOT NULL ,MetaCode TEXT NOT NULL ,MetaType TEXT NOT NULL ,ParentMetaCode TEXT NOT NULL ,Title TEXT NULL ,Path TEXT NULL ,Description TEXT NULL ,DataMetaCode TEXT NULL ,OrderNo INTEGER NULL ,Properties TEXT NULL )


